import { promises } from 'node:fs';
import { createApp, eventHandler, toNodeListener, lazyEventHandler } from './index2.mjs';
import { listen } from './index.mjs';
import { w as writeTypes } from '../shared/nuxi.9175a8b6.mjs';
import { l as loadKit } from '../shared/nuxi.4e484f51.mjs';
import { c as clearDir } from '../shared/nuxi.62d9886b.mjs';
import { o as overrideEnv } from '../shared/nuxi.d21ab543.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi.a865ab6b.mjs';
import { r as resolve, j as join } from '../shared/nuxi.6feb7084.mjs';
import '../shared/nuxi.76077d9c.mjs';
import '../shared/nuxi.e5e3f2b7.mjs';
import 'http';
import 'https';
import 'util';
import 'fs';
import 'os';
import '../shared/nuxi.a3b9dacd.mjs';
import 'tty';
import 'net';
import 'path';
import '../shared/nuxi.c0e643cf.mjs';
import 'child_process';
import '../shared/nuxi.903a28f0.mjs';
import 'node:module';
import 'node:url';
import '../shared/nuxi.350f0fe6.mjs';
import '../shared/nuxi.7d1333e8.mjs';

const analyze = defineNuxtCommand({
  meta: {
    name: "analyze",
    usage: "npx nuxi analyze [rootDir]",
    description: "Build nuxt and analyze production bundle (experimental)"
  },
  async invoke(args) {
    overrideEnv("production");
    const rootDir = resolve(args._[0] || ".");
    const statsDir = join(rootDir, ".nuxt/stats");
    const { loadNuxt, buildNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({
      rootDir,
      config: {
        build: {
          analyze: true
        }
      }
    });
    await clearDir(nuxt.options.buildDir);
    await writeTypes(nuxt);
    await buildNuxt(nuxt);
    const app = createApp();
    const serveFile = (filePath) => lazyEventHandler(async () => {
      const contents = await promises.readFile(filePath, "utf-8");
      return eventHandler((event) => {
        event.res.end(contents);
      });
    });
    console.warn("Do not deploy analyze results! Use `nuxi build` before deploying.");
    console.info("Starting stats server...");
    app.use("/client", serveFile(join(statsDir, "client.html")));
    app.use("/nitro", serveFile(join(statsDir, "nitro.html")));
    app.use(eventHandler(() => `<!DOCTYPE html>
    <html lang="en">
    <head>
    <meta charset="utf-8">
    <title>Nuxt Bundle Stats (experimental)</title>
    </head>
      <h1>Nuxt Bundle Stats (experimental)</h1>
      <ul>
        <li>
          <a href="/nitro">Nitro server bundle stats</a>
        </li>
        <li>
          <a href="/client">Client bundle stats</a>
        </li>
      </ul>
    </html>
    `));
    await listen(toNodeListener(app));
    return "wait";
  }
});

export { analyze as default };
