import { buildNuxt } from '@nuxt/kit';
import { c as consola } from '../shared/nuxi.350f0fe6.mjs';
import { c as clearDir } from '../shared/nuxi.62d9886b.mjs';
import { l as loadKit } from '../shared/nuxi.4e484f51.mjs';
import { w as writeTypes } from '../shared/nuxi.9175a8b6.mjs';
import { d as defineNuxtCommand } from '../shared/nuxi.a865ab6b.mjs';
import { r as resolve, a as relative } from '../shared/nuxi.6feb7084.mjs';
import '../shared/nuxi.7d1333e8.mjs';
import 'util';
import 'path';
import 'fs';
import 'os';
import 'tty';
import 'node:fs';
import '../shared/nuxi.903a28f0.mjs';
import 'node:module';
import 'node:url';
import '../shared/nuxi.c0e643cf.mjs';

const prepare = defineNuxtCommand({
  meta: {
    name: "prepare",
    usage: "npx nuxi prepare",
    description: "Prepare nuxt for development/build"
  },
  async invoke(args) {
    process.env.NODE_ENV = process.env.NODE_ENV || "production";
    const rootDir = resolve(args._[0] || ".");
    const { loadNuxt } = await loadKit(rootDir);
    const nuxt = await loadNuxt({ rootDir, config: { _prepare: true } });
    await clearDir(nuxt.options.buildDir);
    await buildNuxt(nuxt);
    await writeTypes(nuxt);
    consola.success("Types generated in", relative(process.cwd(), nuxt.options.buildDir));
  }
});

export { prepare as default };
